import React, {  useState, useContext } from 'react';
import { UsersContext } from '../context/ListContext';
import {useHistory} from "react-router-dom";

const Login = () => {
    const [email, setEmail] = useState();
    const [password, setPassword] = useState();

    const handleClick = (e) =>{
        e.preventDefault();

    }

    return (
        <div className="container mt-4">
            <form action="Login" className="col-md-4 col-6 m-auto">
                <div className="form-group">
                    <label for="nome">E-mail</label>
                    <input type="email" className="form-control" id="email" onChange={event => setEmail(event.target.value)}/>
                </div>
                <div className="form-group">
                    <label for="nome">Senha</label>
                    <input type="password" className="form-control" id="senha" onChange={event => setPassword(event.target.value)}/>
                </div>
                <button type="submit" className="btn btn-primary w-100" onClick={(event)=>handleClick(event)}>Acessar</button>
            </form>
        </div>
    )
}

export default Login;
